$(function () {
    var t = $("section"),
        a = $("nav"),
        e = a.outerHeight();
    $(window).on("scroll", function () {
        var o = $(this).scrollTop();
        t.each(function () {
            var t = $(this).offset().top - e,
                n = t + $(this).outerHeight();
            o >= t && o <= n && (a.find("li").removeClass("active"), a.find('a[href="#' + $(this).attr("id") + '"]').parent("li").addClass("active"))
        })
    }), a.find("a").on("click", function () {
        var t = $(this).attr("href");
        return $("html, body").animate({
            scrollTop: $(t).offset().top - e + 100
        }, 500), !1
    }), $(".arrow-container a, .app-container a").on("click", function () {
        var t = $(this).attr("href");
        return $("html, body").animate({
            scrollTop: $(t).offset().top - e + 100
        }, 500), !1
    });
    var o = $("#nav").offset().top;
    $(window).scroll(function () {
        $(window).scrollTop() >= o + 50 ? $("#nav").addClass("fixed") : $("#nav").removeClass("fixed")
    }), lastScroll = 0, $(window).on("scroll", function () {
        var t = $(window).scrollTop();
        lastScroll - t > 0 ? $("#nav").addClass("going-up") : $("#nav").removeClass("going-up"), lastScroll = t
    }), $(".reveal").click(function () {
        var t = $(".hidden-content"),
            a = t.height(),
            e = t.css("height", "auto").height();
        t.height(a).animate({
            height: e == a ? "0" : e
        }, 500)
    }),
    $(".hamburger.pull-left").on("click", function () {
        $(".side-manu.pull-left").addClass("open-Menu")
    }), 
    $(".hamburger.pull-right").on("click", function () {
        $(".side-manu.pull-right").addClass("open-Menu")
    }), 
    $(".close-btn").on("click", function () {
        $(".side-manu").removeClass("open-Menu")
    }), 
    $(".side-manu").find("a").on("click", function () {
        $(".side-manu").removeClass("open-Menu");
        var t = $(this).attr("href");
        return $("html, body").animate({
            scrollTop: $(t).offset().top - e + 100
        }, 500), !1
    }), 
    $("#firstModal, #secondModal, #thirdModal, #forthModal, #fifthModal, #sixthModal").on("hidden.bs.modal", function (t) {
        $(".services-container-inner").removeClass("hover")
    });
});